CREATE OR REPLACE FUNCTION obter_se_atend_desfecho ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_atend_desfecho_w	varchar(1) := 'N';


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	begin

	select	CASE WHEN coalesce(count(b.nr_sequencia),0)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ie_atend_desfecho_w
	from	atendimento_alta b, parametro_medico p
	where	nr_atendimento	= nr_atendimento_p
	and     p.cd_estabelecimento = obter_estabelecimento_ativo
	and     ((b.ie_tipo_orientacao <> 'P')
	or (coalesce(p.ie_liberar_desfecho,'N')  = 'N')
	or      ((b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '') and (coalesce(b.dt_inativacao::text, '') = '')));
	end;
end if;
return ie_atend_desfecho_w;
end;
