CREATE OR REPLACE FUNCTION obter_se_atend_des_alta_intern (nr_atendimento_p atendimento_paciente.nr_atendimento%type) RETURNS varchar AS $body$
DECLARE

is_desfecho_w	        varchar(1);
ie_liberar_desfecho_w   parametro_medico.ie_liberar_desfecho%type;

BEGIN

select  coalesce(max(ie_liberar_desfecho),'N') as ie_liberar_desfecho
into STRICT    ie_liberar_desfecho_w
from    parametro_medico
where   cd_estabelecimento = obter_estabelecimento_ativo;

select  CASE WHEN count(nr_sequencia)=0 THEN  'N'  ELSE 'S' END  is_desfecho
into STRICT    is_desfecho_w
from    atendimento_alta
where   nr_atendimento = nr_atendimento_p
and     ie_desfecho in ('I','A')
and		ie_tipo_orientacao = 'P'
and     ( (coalesce(ie_liberar_desfecho_w,'N')  = 'N')
          or ((dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') and (coalesce(dt_inativacao::text, '') = '')));

return is_desfecho_w;

end;
$body$
