SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_atend_encaminhamento (nr_atendimento_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE




-- IE_TIPO_P
--  L  -  Liberar cidad√£o.
--  E -  Encaminhamento especialista.
--  A -  Agendar consulta.
ie_encaminhamento_w	varchar(1) := 'N';


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	if (ie_tipo_p = 'L') then
		select	coalesce(max('S'),'N')
		into STRICT  ie_encaminhamento_w
		from  atendimento_alta a, parametro_medico p
		where  a.nr_atendimento = nr_atendimento_p
		and	  ie_desfecho_consulta = 'L'
		and    p.cd_estabelecimento = obter_estabelecimento_ativo
		and    ((a.ie_tipo_orientacao <> 'P')
		or (coalesce(p.ie_liberar_desfecho,'N')  = 'N')
		or     ((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') and (coalesce(a.dt_inativacao::text, '') = '')));
	elsif (ie_tipo_p = 'E') then
		select	coalesce(max('S'),'N')
		into STRICT  ie_encaminhamento_w
		from  atendimento_alta a, parametro_medico p
		where  a.nr_atendimento = nr_atendimento_p
		and	  ie_desfecho_consulta = 'E'
		and    p.cd_estabelecimento = obter_estabelecimento_ativo
		and    ((a.ie_tipo_orientacao <> 'P')
		or (coalesce(p.ie_liberar_desfecho,'N')  = 'N')
		or     ((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') and (coalesce(a.dt_inativacao::text, '') = '')));

		if (ie_encaminhamento_w = 'N') then
			select	coalesce(max('S'),'N')
			into STRICT	ie_encaminhamento_w
			from	ATEND_ENCAMINHAMENTO
			where	nr_atendimento = nr_atendimento_p
			and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
		end if;

	elsif (ie_tipo_p = 'A') then
		select	coalesce(max('S'),'N')
		into STRICT  ie_encaminhamento_w
		from  atendimento_alta a, parametro_medico p
		where  a.nr_atendimento = nr_atendimento_p
		and	  ie_desfecho_consulta = 'A'
		and    p.cd_estabelecimento = obter_estabelecimento_ativo
		and    ((a.ie_tipo_orientacao <> 'P')
		or (coalesce(p.ie_liberar_desfecho,'N')  = 'N')
		or     ((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') and (coalesce(a.dt_inativacao::text, '') = '')));
	end if;
end if;
return	ie_encaminhamento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
