SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_atend_filtro (nr_atendimento_p bigint, lista_p text) RETURNS varchar AS $body$
DECLARE

 
ie_possui_w					varchar(1) := 'N';
lista_w						varchar(2000);
tam_lista_w					bigint;
ie_pos_virgula_w			smallint;
ie_opcao_w					varchar(50);


BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (lista_p IS NOT NULL AND lista_p::text <> '') then 
	lista_w	:= lista_p;
	 
	while	(lista_w IS NOT NULL AND lista_w::text <> '') loop 
		begin 
		tam_lista_w			:= length(lista_w);
		ie_pos_virgula_w	:= position(',' in lista_w);
		 
		if (ie_pos_virgula_w <> 0) then 
			ie_opcao_w			:= substr(lista_w,1,(ie_pos_virgula_w - 1));
			lista_w				:= substr(lista_w,(ie_pos_virgula_w + 1),tam_lista_w);
		else 
			ie_opcao_w			:= substr(lista_w,1,tam_lista_w);
			lista_w				:= null;
		end if;
		 
		 
		if (ie_opcao_w = 'CR') then 
			if (obter_se_atend_cirurgia(nr_atendimento_p) = 'S') then 
				return 'S';
			end if;
		elsif (ie_opcao_w = 'GR') then 
			if (obter_se_atend_gravidez(nr_atendimento_p) = 'S') then 
				return 'S';
			end if;
		elsif (ie_opcao_w = 'PN') then 
			if (obter_se_atend_prenatal(nr_atendimento_p) = 'S') then 
				return 'S';
			end if;
		elsif (ie_opcao_w = 'EP') then 
			if (obter_se_atend_cpoe_proc(nr_atendimento_p) = 'S') then 
				return 'S';
			end if;
		elsif (ie_opcao_w = 'VA') then 
			if (obter_se_atend_vacina(nr_atendimento_p) = 'S') then 
				return 'S';
			end if;
		elsif (ie_opcao_w = 'IP') then 
			if (obter_se_atend_problema(nr_atendimento_p,'I') = 'S') then 
				return 'S';
			end if;
		elsif (ie_opcao_w = 'FP') then 
			if (obter_se_atend_problema(nr_atendimento_p,'F') = 'S') then 
				return 'S';
			end if;
		elsif (ie_opcao_w = 'RP') then 
			if (obter_se_atend_problema(nr_atendimento_p,'R') = 'S') then 
				return 'S';
			end if;
		elsif (ie_opcao_w = 'EE') then 
			if (obter_se_atend_encaminhamento(nr_atendimento_p,'E') = 'S') then 
				return 'S';
			end if;
		end if;
		end;
	end loop;
	 
	 
end if;
return	ie_possui_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
