CREATE OR REPLACE FUNCTION obter_se_atend_finalizado ( nr_seq_episodio_p bigint, nr_atendimento_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_status_w		atend_paciente_controle.ie_status_atendimento%type;
	
	function get_retorno(ie_status_p	text)
		return char is
	ds_retorno_status_w	varchar(1) := 'N';
	
BEGIN

	if (coalesce(ie_status_p,'T') = 'T') then
		ds_retorno_status_w	:= 'S';
	else
		ds_retorno_status_w	:= 'N';
	end if;
	return	ds_retorno_status_w;
	end;

begin

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (coalesce(nr_atendimento_p::text, '') = '') and (coalesce(nr_seq_episodio_p::text, '') = '') then
	
	begin
	select	max(a.ie_status_atendimento)
	into STRICT	ie_status_w
	from	atend_paciente_controle a
	where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p;
	
	if (coalesce(ie_status_w,'X') = 'X') then
		select	max(a.ie_status_episodio)
		into STRICT	ie_status_w
		from	atend_paciente_controle a
		where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p;
	end if;
	end;
elsif (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	begin
	select	max(a.ie_status_atendimento)
	into STRICT	ie_status_w
	from	atend_paciente_controle a
	where	nr_atendimento		= nr_atendimento_p;
	end;
elsif (nr_seq_episodio_p IS NOT NULL AND nr_seq_episodio_p::text <> '') then
	begin
	select	max(a.ie_status_episodio)
	into STRICT	ie_status_w
	from	atend_paciente_controle a
	where	nr_seq_episodio		= nr_seq_episodio_p;
	end;
end if;

return	get_retorno(ie_status_w);
end;
$body$
