CREATE OR REPLACE FUNCTION obter_se_atend_gravidez (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_gravidez_w	varchar(1) := 'N';


BEGIN


if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
    select	coalesce(max('S'),'N')
	into STRICT  ie_gravidez_w
	from  ciap_atendimento a,
		problema_ciap b,
		componente_ciap d,
		capitulo_ciap c
	where a.cd_ciap			= b.cd_ciap
	and	b.nr_seq_componente = d.nr_sequencia
	and d.nr_seq_capitulo   = c.nr_sequencia
	and	c.cd_capitulo       = 'W'
	and a.nr_atendimento	= nr_atendimento_p;
end if;

return	ie_gravidez_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
