SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_atend_ivc ( nr_atendimento_p bigint, qt_hora_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


ie_ivc_w	char(1);


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_ivc_w
	from	prescr_procedimento a,
			prescr_medica b where		a.nr_prescricao        	= b.nr_prescricao
	and     b.nr_atendimento       	= nr_atendimento_p
	and     obter_se_proc_ivc(a.nr_seq_proc_interno) = 'S'
	and (b.dt_validade_prescr	>= clock_timestamp() - qt_hora_prescr_p / 24)
	and		a.ie_suspenso		= 'N' LIMIT 1;

end if;

return ie_ivc_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
