CREATE OR REPLACE FUNCTION obter_se_atend_lib_financeira ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
nr_atend_mae_w		bigint;

BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then

	select 	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	atendimento_paciente
	where	nr_atendimento = nr_atendimento_p
	and	(dt_liberacao_financeiro IS NOT NULL AND dt_liberacao_financeiro::text <> '');

	if (coalesce(ds_retorno_w,'N') = 'N') then -- Conforme alinhamento feito com o Jefferson o correto seria o sistema consistir se o atendimento for de RN, e caso for buscar a liberação financeira da MÃE
		select	coalesce(max(nr_atendimento_mae),0)
		into STRICT	nr_atend_mae_w
		from 	atendimento_paciente
		where	nr_atendimento = nr_atendimento_p;

		if (coalesce(nr_atend_mae_w,0) > 0) then

			select 	coalesce(max('S'),'N')
			into STRICT	ds_retorno_w
			from	atendimento_paciente
			where	nr_atendimento = nr_atend_mae_w
			and	(dt_liberacao_financeiro IS NOT NULL AND dt_liberacao_financeiro::text <> '');

		end if;

	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
