CREATE OR REPLACE FUNCTION obter_se_atend_parecer ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_parecer_w	bigint;
ds_retorno_w	varchar(100);


BEGIN
select	count(*)
into STRICT	qt_parecer_w
from	parecer_medico_req
where	nr_atendimento = nr_atendimento_p
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	coalesce(dt_inativacao::text, '') = '';

if (qt_parecer_w > 0) then
	begin
	select	count(*)
	into STRICT	qt_parecer_w
	from	parecer_medico
	where	nr_parecer = (	SELECT	max(nr_parecer)
				from	parecer_medico_req
				where	nr_atendimento = nr_atendimento_p
				and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
				and	coalesce(dt_inativacao::text, '') = '');

	if (qt_parecer_w > 0) then
		ds_retorno_w := Wheb_mensagem_pck.get_texto(309793); --'Respondido';
	else
		ds_retorno_w := Wheb_mensagem_pck.get_texto(309794); --'Pendente';
	end if;
	end;
else
	ds_retorno_w := Wheb_mensagem_pck.get_texto(309795); --'NÃ£o possui';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
