CREATE OR REPLACE FUNCTION obter_se_atend_possui_conta ( nr_atendimento_p bigint, ie_tipo_p text default 'T', ie_restricao_p text default 'S', cd_estabelecimento_p bigint default null) RETURNS varchar AS $body$
DECLARE

 
/* ie_tipo_p */
 
-- T - Todos os tipos de conta 
-- P - Somente Particular 
 
/* ie_restricao_p */
 
-- N - Restringe todo o tipo de conta 
-- E - Restring contas do estabelecimento			 
 
ds_retorno_w		varchar(1);
cd_estabelecimento_w	bigint;


BEGIN 
 
cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;
 
If (ie_tipo_p = 'T') then 
	select	coalesce(max('S'),'N') 
	into STRICT	ds_retorno_w 
	from	conta_paciente a 
	where	a.nr_atendimento = nr_atendimento_p 
	and	a.cd_estabelecimento = CASE WHEN ie_restricao_p='E' THEN  coalesce(cd_estabelecimento_p, cd_estabelecimento_w) WHEN ie_restricao_p='N' THEN  a.cd_estabelecimento END;
Elsif (ie_tipo_p = 'P') then 
	select coalesce(max('S'),'N') 
	into STRICT	ds_retorno_w 
	from  conta_paciente a, 
		convenio b 
	where  a.nr_atendimento = nr_atendimento_p 
	and   b.cd_convenio = a.cd_convenio_parametro 
	and   b.ie_tipo_convenio = 1 
	and	a.cd_estabelecimento = CASE WHEN ie_restricao_p='E' THEN  coalesce(cd_estabelecimento_p, cd_estabelecimento_w) WHEN ie_restricao_p='N' THEN  a.cd_estabelecimento END;
End if;
	 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
