CREATE OR REPLACE FUNCTION obter_se_atend_prenatal (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_prenatal_w	varchar(1) := 'N';


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
    	select	coalesce(max('S'),'N')
	into STRICT  ie_prenatal_w
	from  med_pac_pre_natal a,
	    med_pac_pre_natal_evol b
	where  b.nr_seq_pre_natal = a.nr_sequencia
	and	a.nr_atendimento	= nr_atendimento_p;

end if;

return	ie_prenatal_w;

end;
