CREATE OR REPLACE FUNCTION obter_se_atend_prescr_adep (nr_atend_adep_p bigint, cd_setor_adep_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_prescr_adep_w	varchar(1);


BEGIN 
if (nr_atend_adep_p IS NOT NULL AND nr_atend_adep_p::text <> '') and (cd_setor_adep_p IS NOT NULL AND cd_setor_adep_p::text <> '') then 
 
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ie_prescr_adep_w 
	from	adep_pend_v 
	where	nr_atendimento	= nr_atend_adep_p 
	and	cd_setor_prescr	= cd_setor_adep_p 
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
	and	dt_validade_prescr	> clock_timestamp() 
	and	dt_horario		> clock_timestamp();
 
end if;
 
return ie_prescr_adep_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
