CREATE OR REPLACE FUNCTION obter_se_atend_prescr_vig ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_prescr_vig_w		char(1) := 'N';


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_prescr_vig_w
	from	prescr_medica where		nr_atendimento = nr_atendimento_p
	and		clock_timestamp() between dt_inicio_prescr and dt_validade_prescr LIMIT 1;

end if;

return	ie_prescr_vig_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
