CREATE OR REPLACE FUNCTION obter_se_atend_servico_hoje ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(1) := 'N';


BEGIN
	if (coalesce(nr_atendimento_p,0) > 0) then

		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from   	nut_atend_serv_dia
		where  	nr_atendimento = nr_atendimento_p
		and   	trunc(dt_servico) = trunc(clock_timestamp());

	end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
