CREATE OR REPLACE FUNCTION obter_se_atend_status ( nr_atendimento_p bigint, ie_status_p text) RETURNS varchar AS $body$
DECLARE

 
ie_acolhimento_w		varchar(1);


BEGIN 
select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
into STRICT	ie_acolhimento_w 
FROM	atendimento_paciente a, 
		pessoa_fisica b 
WHERE 	a.cd_pessoa_fisica = b.cd_pessoa_fisica 
AND		a.ie_nivel_atencao = 'P' 
and		a.nr_atendimento = nr_atendimento_p 
and		obter_status_atend_pc(a.nr_atendimento, 'C') = ie_status_p;
 
 
return	ie_acolhimento_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
