SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_atend_sus ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w			varchar(1)	:= 'N';
cd_convenio_sus_w		integer;
ie_nivel_atencao_w  	varchar(1)	:= 'T';


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	Select  wheb_assist_pck.get_nivel_atencao_perfil
	into STRICT	ie_nivel_atencao_w
	;

	if ( ie_nivel_atencao_w in ('P','S')) then

		select	max(cd_convenio_sus)
		into STRICT	cd_convenio_sus_w
		from	parametro_faturamento
		where	cd_estabelecimento	= wheb_usuario_pck.get_cd_estabelecimento;

		Select  coalesce(max('S'),'N')
		into STRICT	ds_retorno_w
		from	atendimento_paciente a,
				atend_categoria_convenio b
		where	a.nr_atendimento =  nr_atendimento_p
		and		a.nr_atendimento =  b.nr_atendimento
		and		a.ie_tipo_atendimento	= 1
		and		a.ie_tipo_convenio	= 3
		and		b.cd_convenio = cd_convenio_sus_w;

	end if;

end if;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_atend_sus ( nr_atendimento_p bigint) FROM PUBLIC;

