CREATE OR REPLACE FUNCTION obter_se_atestado_lib (nr_atendimento_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w		varchar(10) := 'S';
qt_conta_w		bigint := 0;
cd_empresa_w		bigint;
cd_dependente_w		bigint;
ie_tipo_atendimento_w	smallint;


BEGIN 
 
select 	max(cd_empresa), 
	coalesce(max(ie_tipo_atendimento),0), 
	coalesce(max(cd_dependente),0) 
into STRICT	cd_empresa_w, 
	ie_tipo_atendimento_w, 
	cd_dependente_w 
from 	atendimento_paciente_v 
where 	nr_atendimento = nr_atendimento_p;
 
if (cd_empresa_w IS NOT NULL AND cd_empresa_w::text <> '') then	 
	select 	count(*) 
	into STRICT	qt_conta_w 
	from  	empresa_ref_tipo_atestado 
	where	cd_empresa_ref	=	cd_empresa_w;
 
	if (qt_conta_w	 > 0)	 then 
		select 	count(*) 
		into STRICT	qt_conta_w 
		from  	empresa_ref_tipo_atestado 
		where	cd_empresa_ref	=	cd_empresa_w 
		and	coalesce(cd_dependente, coalesce(cd_dependente_w,0)) = cd_dependente_w 
		and	coalesce(ie_tipo_atendimento, coalesce(ie_tipo_atendimento_w,0)) = ie_tipo_atendimento_w 
		and	nr_seq_tipo_atestado = nr_sequencia_p;
		 
		if (qt_conta_w	= 0) then 
			ie_retorno_w	:= 'N';
		end if;
	end if;
end if;
 
return ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
