CREATE OR REPLACE FUNCTION obter_se_atestado_padrao_lib (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

			 
ds_retorno_w	varchar(10)	:= 'S';
qt_registro_w	bigint;
cd_pessoa_fisica_w	bigint;
cd_perfil_w		bigint;


BEGIN 
 
select	max(cd_pessoa_fisica) 
into STRICT	cd_pessoa_fisica_w 
from	usuario 
where	nm_usuario	= wheb_usuario_pck.get_nm_usuario;
 
cd_perfil_w	:= obter_perfil_ativo;
 
select	count(*) 
into STRICT	qt_registro_w 
from	tipo_atestado_regra 
where	nr_seq_tipo_atestado	= nr_sequencia_p;
 
if (qt_registro_w	> 0) then 
	select	count(*) 
	into STRICT	qt_registro_w 
	from	tipo_atestado_regra 
	where	nr_seq_tipo_atestado	= nr_sequencia_p 
	and	coalesce(cd_perfil,cd_perfil_w)	= cd_perfil_w 
	and	((coalesce(cd_especialidade::text, '') = '') or (obter_se_especialidade_medico(cd_pessoa_fisica_w,cd_especialidade)	= 'S') );
	 
	if (qt_registro_w	= 0) then 
		ds_retorno_w	:= 'N';
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
