CREATE OR REPLACE FUNCTION obter_se_ate_prescr_urgente ( nr_atendimento_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type) RETURNS varchar AS $body$
DECLARE


ie_urgente_w		varchar(1);


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	CASE WHEN count(nr_prescricao)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_urgente_w
	from	prescr_medica
	where	nr_atendimento = nr_atendimento_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	coalesce(ie_lib_farm,'N') = 'S'
	and	coalesce(dt_liberacao_farmacia::text, '') = ''
	and	obter_se_prioridade_revisao(nr_prescricao)  = 'S'
	and	dt_prescricao between dt_inicial_p and dt_Final_p  LIMIT 1;
else
	select	CASE WHEN count(nr_prescricao)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_urgente_w
	from	prescr_medica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and coalesce(nr_atendimento::text, '') = ''
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	coalesce(ie_lib_farm,'N') = 'S'
	and	coalesce(dt_liberacao_farmacia::text, '') = ''
	and	obter_se_prioridade_revisao(nr_prescricao)  = 'S'
	and	dt_prescricao between dt_inicial_p and dt_Final_p  LIMIT 1;
end if;

return	ie_urgente_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
