CREATE OR REPLACE FUNCTION obter_se_ativa_consulta_senhas (nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_visualiza_w		bigint;
dt_ultima_senha_w	timestamp;


BEGIN

begin
	select	1
	into STRICT	ie_visualiza_w
	from	visualizacao_conulta_senha
	where	nm_usuario = nm_usuario_p;
exception
	when	no_data_found then
		ie_visualiza_w := 0;
end;

if (ie_visualiza_w = 0) then
	return 'S';
else
	select	max(dt_geracao_senha)
	into STRICT	dt_ultima_senha_w
	from	paciente_senha_fila;

	begin
		select	1
		into STRICT	ie_visualiza_w
		from	visualizacao_conulta_senha
		where	nm_usuario = nm_usuario_p
		and	dt_visualizacao <= dt_ultima_senha_w;
	exception
		when	no_data_found then
			ie_visualiza_w := 0;
	end;

	If (ie_visualiza_w = 0) then
		return 'N';
	elsif (ie_visualiza_w = 1) then
		return 'S';
	else
		return 'N';
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
