CREATE OR REPLACE FUNCTION obter_se_ativ_milestone (nr_seq_etapa_p bigint, nr_seq_milestone_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
nr_seq_superior_w	proj_cron_etapa.nr_seq_superior%type;


BEGIN

select	max(nr_seq_superior)
into STRICT	nr_seq_superior_w
from	proj_cron_etapa
where	nr_sequencia = nr_seq_etapa_p
and		(nr_seq_superior IS NOT NULL AND nr_seq_superior::text <> '');

if (nr_seq_superior_w IS NOT NULL AND nr_seq_superior_w::text <> '') then
	if (nr_seq_superior_w = nr_seq_milestone_p) then
		ds_retorno_w := 'S';
	else
		ds_retorno_w := obter_se_ativ_milestone(nr_seq_superior_w,nr_seq_milestone_p);
	end if;
else
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
