CREATE OR REPLACE FUNCTION obter_se_ativ_os_ativ_proj (nr_seq_os_p bigint) RETURNS varchar AS $body$
DECLARE


ie_ativ_proj_w	varchar(1) := 'N';


BEGIN
if (nr_seq_os_p IS NOT NULL AND nr_seq_os_p::text <> '') then
	begin
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_ativ_proj_w
	from	man_ordem_ativ_etapa_v a
	where	a.nr_seq_ordem = nr_seq_os_p
	and	not exists (	SELECT	1
				from	proj_projeto x
				where	x.nr_seq_ordem_serv = nr_seq_os_p);
	end;
end if;
return ie_ativ_proj_w;
end;
$body$
