CREATE OR REPLACE FUNCTION obter_se_atrib_estrut_conta (cd_convenio_p bigint, nm_atributo_p text, cd_estrutura_p bigint) RETURNS varchar AS $body$
DECLARE


ie_atrib_w	varchar(05)	:= 'N';


BEGIN

select 	coalesce(max('S'), 'N')
into STRICT	ie_atrib_w
from   	convenio_estrutura_conta b,
	convenio_estrut_atrib a
where  	a.nr_sequencia = b.nr_sequencia
and    	b.cd_estrutura = cd_estrutura_p
and    	b.cd_convenio  = cd_convenio_p
and    	a.nm_atributo  = nm_atributo_p;

if (ie_atrib_w = 'N') then
	select 	coalesce(max('S'), 'N')
	into STRICT	ie_atrib_w
	from   	convenio_estrutura_conta b,
		convenio_estrut_atrib a
	where  	a.nr_sequencia = b.nr_sequencia
	and    	b.cd_estrutura = cd_estrutura_p
	and	coalesce(b.cd_convenio::text, '') = ''
	and    	a.nm_atributo  = nm_atributo_p;
end if;

return	ie_atrib_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
