CREATE OR REPLACE FUNCTION obter_se_atualiza_campo ( nr_prescricao_p bigint, cd_material_p bigint, ie_via_aplicacao_p text, ie_se_necessario_p text) RETURNS varchar AS $body$
DECLARE

			 
ie_retorno_w			char(1) := 'N';
cd_setor_atendimento_w	bigint;
cd_pessoa_fisica_w		varchar(50);
qt_idade_w				bigint;
qt_peso_w				double precision;
nr_atendimento_w		bigint;


BEGIN 
 
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then 
	select	max(a.cd_setor_atendimento), 
			max(a.cd_pessoa_fisica), 
			max(obter_idade_pf(a.cd_pessoa_fisica, clock_timestamp(), 'A')), 
			max(a.qt_peso), 
			max(nr_atendimento) 
	into STRICT	cd_setor_atendimento_w, 
			cd_pessoa_fisica_w, 
			qt_idade_w, 
			qt_peso_w, 
			nr_atendimento_w 
	from	prescr_medica	a 
	where	a.nr_prescricao = nr_prescricao_p;
 
	if (coalesce(Obter_Padrao_Param_Prescr(nr_atendimento_w,cd_material_p, ie_via_aplicacao_p, cd_setor_atendimento_w, cd_pessoa_fisica_w, qt_idade_w, qt_peso_w, ie_se_necessario_p, 'D', null)::text, '') = '') then 
		select	CASE WHEN coalesce(max(qt_dose_prescricao)::text, '') = '' THEN  'S'  ELSE 'N' END  
		into STRICT	ie_retorno_w 
		from	material 
		where	cd_material = cd_material_p;
	end if;
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
