CREATE OR REPLACE FUNCTION obter_se_atualiza_cod_conv ( cd_convenio_p bigint, ie_tipo_auditoria_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1):= 'S';
ie_converte_itens_audit_w varchar(1):= 'S';


BEGIN

select	max(coalesce(ie_converte_itens_audit,'S'))
into STRICT	ie_converte_itens_audit_w
from	convenio_estabelecimento
where	cd_convenio = cd_convenio_p
and	cd_estabelecimento = cd_estabelecimento_p;

if (ie_converte_itens_audit_w = 'N' AND ie_tipo_auditoria_p = 'S') then
	ie_retorno_w:= 'N';
else
	ie_retorno_w:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
