CREATE OR REPLACE FUNCTION obter_se_auditoria_alerta ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1)	:= 'N';
qt_alertas_w	bigint	:= 0;


BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then

	select	count(*)
	into STRICT	qt_alertas_w
	from	alerta_auditoria_conpaci
	where	nr_atendimento = nr_atendimento_p
	and	clock_timestamp() between dt_alerta and coalesce(dt_fim_alerta,clock_timestamp())
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	ie_situacao = 'A';

	if (qt_alertas_w > 0) then
		ie_retorno_w	:= 'S';
	end if;

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
