CREATE OR REPLACE FUNCTION obter_se_autorizado_inventario (nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
ie_tipo_inventario_w	varchar(1);
ds_retorno_w		varchar(15) := 'N';
			

BEGIN 
 
select	max(ie_tipo_inventario) 
into STRICT	ie_tipo_inventario_w 
from	sup_autoriza_inventario 
where	obter_pessoa_fisica_usuario(nm_usuario_p,'C') = cd_pessoa_autorizada 
and	clock_timestamp() between dt_inicio and dt_final 
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
 
if (ie_tipo_inventario_w IS NOT NULL AND ie_tipo_inventario_w::text <> '') then 
	ds_retorno_w := ie_tipo_inventario_w;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
