CREATE OR REPLACE FUNCTION obter_se_autor_acesso (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_perfil_w		bigint;
ie_autoriza_acesso_w	varchar(5) := 'S';


BEGIN

select 	max(nr_seq_perfil)
into STRICT	nr_seq_perfil_w
from	pessoa_fisica
where	cd_pessoa_fisica = cd_pessoa_fisica_p;

if (nr_seq_perfil_w > 0) then

	select	max(ie_autorizar_acesso)
	into STRICT	ie_autoriza_acesso_w
	from	PEP_PERFIL_PACIENTE
	where	nr_sequencia = nr_seq_perfil_w;

end if;


return	ie_autoriza_acesso_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
