CREATE OR REPLACE FUNCTION obter_se_autor_periodo_agendas (nr_seq_agenda_p bigint, dt_agenda_integ_p timestamp, dt_agenda_p timestamp) RETURNS bigint AS $body$
DECLARE


ie_retorno_w		bigint := 9999;
dt_agendamento_w	timestamp;


BEGIN
if (dt_agenda_integ_p IS NOT NULL AND dt_agenda_integ_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') then
	ie_retorno_w := Obter_Dias_Entre_Datas(dt_agenda_integ_p,dt_agenda_p);
elsif (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') and (coalesce(dt_agenda_integ_p::text, '') = '') then

	select	max(a.dt_agendamento)
	into STRICT	dt_agendamento_w
	from	agenda_paciente a
	where	a.nr_sequencia = nr_seq_agenda_p;

	if (dt_agendamento_w IS NOT NULL AND dt_agendamento_w::text <> '') then
		ie_retorno_w := Obter_Dias_Entre_Datas(dt_agendamento_w,dt_agenda_p);
	end if;

end if;


return	ie_retorno_w;

end;
$body$
