CREATE OR REPLACE FUNCTION obter_se_auto_higienizacao ( cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



nr_sequencia_w		bigint;
qt_existe_w		bigint := 0;
ie_higienizar_w		varchar(1) := 'N';

BEGIN


select	max(nr_seq_interno)
into STRICT	nr_sequencia_w
from	unidade_atendimento
where	cd_setor_atendimento	= cd_setor_atendimento_p
and	cd_unidade_basica	= cd_unidade_basica_p
and	cd_unidade_compl	= cd_unidade_compl_p;


if (nr_sequencia_w > 0) and (ie_opcao_p = 'T') then

	begin
	select count(*)
	into STRICT	qt_existe_w
	from	regra_higienizacao_setor
	where	coalesce(nr_seq_interno,nr_sequencia_w)	         = nr_sequencia_w
	and	coalesce(cd_setor_atendimento,cd_setor_atendimento_p) = cd_setor_atendimento_p
	and	coalesce(ie_considera_transf,'S')  			 = 'S';
	end;
elsif (nr_sequencia_w > 0) and (ie_opcao_p = 'S') then

	begin
	select count(*)
	into STRICT	qt_existe_w
	from	regra_higienizacao_setor
	where	coalesce(nr_seq_interno,nr_sequencia_w)	         = nr_sequencia_w
	and	coalesce(cd_setor_atendimento,cd_setor_atendimento_p) = cd_setor_atendimento_p
	and	coalesce(ie_considera_saida,'N') 			 = 'S';
	end;
end if;

if (qt_existe_w > 0) then
	ie_higienizar_w	:=	'S';
end if;

return ie_higienizar_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
