CREATE OR REPLACE FUNCTION obter_se_avaliacao_cadastrada (nr_sequencia_p bigint, cd_pessoa_fisica_p text, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_alerta_avaliacao_w		varchar(5);
qt_avaliacao_paciente_w		bigint;
ie_avalicao_cadastrada_w	varchar(1) 	:= 'N';
					

BEGIN 
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	begin 
		select	coalesce(max(ie_alerta_avaliacao),'N') 
		into STRICT	ie_alerta_avaliacao_w 
		from	evento_cirurgia 
		where	nr_sequencia = nr_sequencia_p 
		and	Obter_se_medico(cd_pessoa_fisica_p, 'M') = 'S';
		 
		select	count(*) 
		into STRICT 	qt_avaliacao_paciente_w 
		from 	med_avaliacao_paciente 
		where 	nr_atendimento = nr_atendimento_p;
		 
		if (ie_alerta_avaliacao_w 		= 'S') 	and (qt_avaliacao_paciente_w 	= 0)	then 
			begin 
			ie_avalicao_cadastrada_w	:= 'S';
			end;
		end if;
	end;
end if;
 
return ie_avalicao_cadastrada_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
