CREATE OR REPLACE FUNCTION obter_se_avaliacao_desfecho (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_setor_atendimento_w		integer;
nr_seq_tipo_avaliacao_w		bigint;
nr_total_w			bigint	:= 1;
ds_avaliacao_w			varchar(200);
ds_retorno_w			varchar(200);
cd_doenca_cid_w			varchar(10);

c02 CURSOR FOR 
SELECT	nr_seq_avaliacao, 
	cd_doenca_cid 
from	setor_desfecho_aval 
where	cd_setor_atendimento = cd_setor_atendimento_w;


BEGIN 
 
select	obter_setor_atendimento(nr_atendimento_p) 
into STRICT	cd_setor_atendimento_w
;
 
OPEN C02;
LOOP 
FETCH C02 into 	nr_seq_tipo_avaliacao_w, 
		cd_doenca_cid_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
	if (obter_se_cid_atendimento(nr_atendimento_p,cd_doenca_cid_w) = 'S') then 
		begin 
		select	count(*) 
		into STRICT	nr_total_w 
		from	med_avaliacao_paciente 
		where	nr_seq_tipo_avaliacao 	= nr_seq_tipo_avaliacao_w 
		and	nr_atendimento 		= nr_atendimento_p;
		end;
	end if;
	 
	if (nr_total_w = 0) then 
		exit;
	end if;
 
END LOOP;
CLOSE C02;
 
select	substr(obter_descricao_padrao('MED_TIPO_AVALIACAO','DS_TIPO', nr_seq_tipo_avaliacao_w),1,100) 
into STRICT	ds_avaliacao_w
;
 
if (nr_total_w = 0)	then 
	ds_retorno_w := Wheb_mensagem_pck.get_texto(309800, 'DS_AVALIACAO_W='||ds_avaliacao_w); --'Para fazer o desfecho é obrigatório o preenchimento da avaliação: '||ds_avaliacao_w||'.'; 
else	
	ds_retorno_w := '';
end if;
 
return	 ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_avaliacao_desfecho (nr_atendimento_p bigint) FROM PUBLIC;

