CREATE OR REPLACE FUNCTION obter_se_avaliacao_pac_lib (NR_PRESCRICAO_P MED_AVALIACAO_PACIENTE.NR_PRESCRICAO%type, NR_SEQ_PRESCR_P MED_AVALIACAO_PACIENTE.NR_SEQ_PRESCR%type) RETURNS varchar AS $body$
DECLARE

RESULT_W varchar(1);

BEGIN
  SELECT CASE WHEN COUNT(*)=0 THEN  'N'  ELSE 'S' END
  INTO STRICT RESULT_W 
  FROM MED_AVALIACAO_PACIENTE
  WHERE NR_PRESCRICAO = NR_PRESCRICAO_P
    AND NR_SEQ_PRESCR = NR_SEQ_PRESCR_P
    AND ((DT_LIBERACAO IS NOT NULL AND DT_LIBERACAO::text <> '')
    OR (DT_INATIVACAO IS NOT NULL AND DT_INATIVACAO::text <> ''));
RETURN RESULT_W;
END;
