CREATE OR REPLACE FUNCTION obter_se_avaliacao_pf (nr_seq_avaliacao_p bigint, cd_pessoa_fisica_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_w	varchar(1) := 'S';
qt_medico_w	bigint;
nm_usuario_w	varchar(15);
qt_grupo_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_medico_w
from	med_medico_avaliacao
where	nr_seq_tipo_aval	= nr_seq_avaliacao_p
and	(cd_medico IS NOT NULL AND cd_medico::text <> '');

select 	count(*)
into STRICT 	qt_grupo_w
from	med_medico_avaliacao
where	nr_seq_tipo_aval	= nr_seq_avaliacao_p
and	(nr_seq_grupo_usuario IS NOT NULL AND nr_seq_grupo_usuario::text <> '');

SELECT  max(SUBSTR(OBTER_USUARIO_PF(cd_pessoa_fisica_p),1,255))
into STRICT 	nm_usuario_w
;

if (qt_medico_w > 0) then
	begin
	select	coalesce(max('S'),'N')
	into STRICT	ie_permite_w
	from	med_medico_avaliacao
	where	nr_seq_tipo_aval	= nr_seq_avaliacao_p
	and	cd_medico		= cd_pessoa_fisica_p
	and	((coalesce(nr_seq_grupo_usuario::text, '') = '') or (OBTER_SE_USUARIO_GRUPO(nr_seq_grupo_usuario,nm_usuario_w)	= 'S'));
	end;
elsif (qt_grupo_w > 0) then
	begin
	select	coalesce(max('S'),'N')
	into STRICT	ie_permite_w
	from	med_medico_avaliacao
	where	nr_seq_tipo_aval		  = nr_seq_avaliacao_p
	and	coalesce(cd_medico,cd_pessoa_fisica_p) = cd_pessoa_fisica_p
	and (OBTER_SE_USUARIO_GRUPO(nr_seq_grupo_usuario,nm_usuario_w)	= 'S');
	end;
end if;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
