CREATE OR REPLACE FUNCTION obter_se_aval_pac_lib (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

ie_possui_w	varchar(1);
ie_avaliacao_w	varchar(255);


BEGIN 
ie_avaliacao_w := Obter_Param_Usuario(3130, 354, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_avaliacao_w);
 
ie_possui_w := 'S';
 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (ie_avaliacao_w IS NOT NULL AND ie_avaliacao_w::text <> '') then 
	select coalesce(max('S'),'N') 
	into STRICT	ie_possui_w 
	from 	med_avaliacao_paciente 
	where	cd_pessoa_fisica = cd_pessoa_fisica_p 
	and 	dt_avaliacao >= clock_timestamp() - interval '1 days' 
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
	and 	obter_se_contido(nr_seq_tipo_avaliacao, ie_avaliacao_w) = 'S';
end if;
 
return	ie_possui_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
