CREATE OR REPLACE FUNCTION obter_se_avf_tipo_lib ( nr_seq_tipo_p bigint, nr_seq_nota_p bigint default null) RETURNS varchar AS $body$
DECLARE


ie_liberada_w		varchar(01);
ie_existe_w		varchar(01);
cd_perfil_w		integer := obter_perfil_ativo;
cd_tipo_pessoa_w		pessoa_juridica.cd_tipo_pessoa%type;

c01 CURSOR FOR
SELECT	'S'
from	avf_tipo_aval_lib
where	nr_seq_tipo_aval = nr_seq_tipo_p
and	coalesce(cd_perfil,cd_perfil_w) = cd_perfil_w;


BEGIN
ie_liberada_w := 'N';

begin
select	'S'
into STRICT	ie_existe_w
from	avf_tipo_aval_lib
where	nr_seq_tipo_aval = nr_seq_tipo_p  LIMIT 1;
exception
when others then
	ie_existe_w 	:= 'N';
	ie_liberada_w	:= 'S';
end;

if (ie_existe_w = 'S') then
	open C01;
	loop
	fetch C01 into
		ie_liberada_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		null;
		end;
	end loop;
	close C01;
end if;

if (not(coalesce(nr_seq_nota_p::text, '') = '')) and (ie_liberada_w = 'S') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_existe_w
	from	avf_tipo_av_pj
	where	nr_seq_tipo_aval = nr_seq_tipo_p;

	if ie_existe_w = 'S' then

		select 	coalesce(max(b.cd_tipo_pessoa),0)
		into STRICT	cd_tipo_pessoa_w
		from 	nota_fiscal a,
			pessoa_juridica b
		where 	a.cd_cgc = b.cd_cgc
		and 	a.nr_sequencia = nr_seq_nota_p;

		select 	coalesce(max('S'),'N')
		into STRICT	ie_liberada_w
		from   	avf_tipo_av_pj
		where  	nr_seq_tipo_aval = nr_seq_tipo_p
		and    	coalesce(cd_tipo_pessoa, cd_tipo_pessoa_w) = cd_tipo_pessoa_w;

	end if;

end if;

return	ie_liberada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
