CREATE OR REPLACE FUNCTION obter_se_avisa_dieta ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_avisa_dieta_w	varchar(1) := 'N';


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	begin

	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ie_avisa_dieta_w
	from	prescr_dieta
	where	nr_prescricao = nr_prescricao_p;

	if (ie_avisa_dieta_w = 'S') then
		begin

		select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_avisa_dieta_w
		from	rep_jejum
		where	nr_prescricao = nr_prescricao_p;
		end;
	end if;
	end;
end if;
return ie_avisa_dieta_w;
end;
$body$
