CREATE OR REPLACE FUNCTION obter_se_avisa_val_lote_regra ( cd_material_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
cd_grupo_material_w	smallint;
cd_subgrupo_material_w	smallint;
cd_classe_material_w	integer;
ie_local_estoque_lote_w	varchar(1);
ie_avisa_w		varchar(1);


BEGIN

select 	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v
where	cd_material = cd_material_p;

select	substr(coalesce(max(ie_avisa),'N'),1,1),
	substr(coalesce(max(ie_local_estoque_lote),'N'),1,1)
into STRICT	ds_retorno_w,
	ie_local_estoque_lote_w
from (SELECT	coalesce(ie_avisa_validade_lote,'N') ie_avisa,
		coalesce(ie_local_estoque_lote,'N') ie_local_estoque_lote
	from	regra_aviso_val_lote
	where	ie_situacao = 'A'
	and	cd_estabelecimento = cd_estabelecimento_p
	and	coalesce(cd_material, cd_material_p) = cd_material_p
	and	coalesce(cd_grupo_material, cd_grupo_material_w) = cd_grupo_material_w
	and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w) = cd_subgrupo_material_w
	and	coalesce(cd_classe_material, cd_classe_material_w) = cd_classe_material_w
	order by cd_material,
		cd_classe_material,
		cd_subgrupo_material,
		cd_grupo_material) alias12 LIMIT 1;

if (ds_retorno_w = 'S') and (ie_local_estoque_lote_w = 'S') then
	ds_retorno_w := 'L';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
