CREATE OR REPLACE FUNCTION obter_se_aviso_agenda (cd_agenda_p bigint, dt_aviso_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_aviso_w	varchar(1);


BEGIN
if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (dt_aviso_p IS NOT NULL AND dt_aviso_p::text <> '') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_aviso_w
	from	agenda_aviso
	where	cd_agenda 	= cd_agenda_p
	and	dt_aviso	= dt_aviso_p
	and	(ds_aviso IS NOT NULL AND ds_aviso::text <> '');
end if;

return ie_aviso_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
