SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_banco_fechado ( nr_seq_conta_banco_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_fechado_w	varchar(1)	:= 'N';
dt_inicial_w	timestamp;
dt_final_w	timestamp;


BEGIN

dt_inicial_w	:= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_referencia_p);
dt_final_w	:= fim_dia(dt_referencia_p);

select	coalesce(max('S'),'N')
into STRICT	ie_fechado_w
from	fechamento_banco a
where	a.dt_fechamento		between dt_inicial_w and dt_final_w
and	a.nr_seq_conta_banco	= nr_seq_conta_banco_p;

if (ie_fechado_w	= 'N') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_fechado_w
	from	fechamento_banco a
	where	coalesce(a.nr_seq_conta_banco::text, '') = ''
	and	a.dt_fechamento		between dt_inicial_w and dt_final_w;

end if;

RETURN	ie_fechado_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
