CREATE OR REPLACE FUNCTION obter_se_bloqueia_reserva ( nr_seq_nut_reserva_p bigint) RETURNS varchar AS $body$
DECLARE


hr_limite_w		timestamp;
ds_retorno_w		varchar(1);


BEGIN

select	to_date(to_char(dt_servico,'dd/mm/yyyy') || ' ' || to_char(hr_inicio,'hh24:mi:ss'), 'dd/mm/yyyy hh24:mi:ss') - (1/1440 * qt_min_antecedencia)
into STRICT	hr_limite_w
from	nut_reserva
where	nr_sequencia = nr_seq_nut_reserva_p;

if (clock_timestamp() > hr_limite_w) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
