CREATE OR REPLACE FUNCTION obter_se_bolsa_inutil ( nr_seq_doacao_p bigint) RETURNS varchar AS $body$
DECLARE


qtd_w 		integer;
ie_retorno_w	varchar(1) := 'N';

BEGIN

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then

	select 	count(*)
	into STRICT	qtd_w
	from  	san_producao
	where 	nr_seq_doacao = nr_seq_doacao_p;

	if (qtd_w > 0) then
		select 	count(*)
		into STRICT	qtd_w
		from  	san_producao
		where 	nr_seq_doacao = nr_seq_doacao_p
		AND	coalesce(nr_seq_inutil::text, '') = '';

		if (qtd_w = 0) then
			ie_retorno_w := 'S';
		else
			ie_retorno_w := 'N';
		end if;
	else
		ie_retorno_w := 'N';
	end if;

	if (ie_retorno_w = 'N') then
		select	san_obter_se_doacao_inutil(nr_seq_doacao_p)
		into STRICT	ie_retorno_w
		;
	end if;

end if;

return ie_retorno_w;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
