SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_bolsa_paciente (nr_bolsa_p text, nr_seq_derivado_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(10) := 'S';
cont_w		bigint;
nr_bolsa_w	varchar(50);


BEGIN

select	count(*)
into STRICT	cont_w
from	san_derivado_bolsa
where	nr_seq_derivado	= nr_seq_derivado_p;

if (cont_w	> 0) then

	select	max(nr_bolsa)
	into STRICT	nr_bolsa_w
	from	san_derivado_bolsa
	where	nr_seq_derivado	= nr_seq_derivado_p
	and	nr_bolsa	= upper(substr(nr_bolsa_p,1,length(nr_bolsa_p)-3));

	if (coalesce(nr_bolsa_w::text, '') = '') then
		ds_retorno_w	:= 'N';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
