CREATE OR REPLACE FUNCTION obter_se_bsnr_existe (cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, nr_bsnr_p medico_espec_bsnr.nr_bsnr%type) RETURNS varchar AS $body$
DECLARE


ie_existe_w 	varchar(1) := 'N';


BEGIN    
	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nr_bsnr_p IS NOT NULL AND nr_bsnr_p::text <> '') then
		select  coalesce(max('S'),'N')
		into STRICT	ie_existe_w
		from 	medico_espec_bsnr 
		where   cd_pessoa_fisica = cd_pessoa_fisica_p
		and 	nr_bsnr = nr_bsnr_p;
	end if;
	
	return ie_existe_w;
	
end;
