CREATE OR REPLACE FUNCTION obter_se_cadastro_medic_prm (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'S';
qt_registro_w	bigint;
ie_alto_risco_w	varchar(1);


BEGIN

Select	coalesce(max(ie_alto_risco),'N')
into STRICT	ie_alto_risco_w
from	prm_ficha_ocorrencia
where	nr_seq_evento = nr_sequencia_p;

if ( ie_alto_risco_w = 'S' ) then

	Select	count(*)
	into STRICT	qt_registro_w
	from	prm_ficha_material a,
		prm_ficha_ocorrencia b
	where	a.nr_seq_ficha = b.nr_Sequencia
	and	b.nr_seq_evento = nr_sequencia_p
	and	obter_se_material_risco(wheb_usuario_pck.get_cd_setor_atendimento,a.cd_material) = 'S';

	if ( qt_registro_w = 0 ) then
		ie_retorno_w := 'N';
	end if;

end if;

return ie_retorno_w;

end;
