CREATE OR REPLACE FUNCTION obter_se_cad_tipo_alerta_pf ( nr_tipo_alerta_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) 	:= 'S';
qt_regra_w		bigint;
cd_perfil_w		perfil.cd_perfil%type	:= obter_perfil_ativo;
cd_setor_atendimento_w  setor_atendimento.cd_setor_atendimento%type := wheb_usuario_pck.get_cd_setor_atendimento;
cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;
cd_funcao_w		funcao.cd_funcao%type 	:= wheb_usuario_pck.get_cd_funcao;
cd_estabelecimento_w smallint;

C01 CURSOR FOR
	SELECT	ie_cadastro
	from	tipo_alerta_atend_lib
	where	nr_seq_tipo_alerta = nr_tipo_alerta_p
	and	coalesce(coalesce(cd_perfil,cd_perfil_w),0) = coalesce(cd_perfil_w,0)
	and	coalesce(coalesce(cd_setor_atendimento,cd_setor_atendimento_w),0) = coalesce(cd_setor_atendimento_w,0)
	and	coalesce(coalesce(cd_funcao,cd_funcao_w),0) = coalesce(cd_funcao_w,0)
	and	coalesce(cd_pessoa_fisica,cd_pessoa_fisica_w)	= cd_pessoa_fisica_w
     and  coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
	order by	coalesce(cd_pessoa_fisica,'0'),
			coalesce(cd_perfil,0),
			coalesce(cd_setor_atendimento,0),
			coalesce(cd_funcao,0);


BEGIN
if (nr_tipo_alerta_p > 0) then
	select 	count(*)
	into STRICT	qt_regra_w
	from	tipo_alerta_atend_lib
	where	nr_seq_tipo_alerta = nr_tipo_alerta_p;

	if (qt_regra_w > 0) then
		begin
		ie_retorno_w	:= 'N';

		select  coalesce(max(cd_pessoa_fisica),'0')
		into STRICT    cd_pessoa_fisica_w
		from    usuario
		where   nm_usuario      = nm_usuario_p;

          cd_estabelecimento_w := Obter_estab_usuario(nm_usuario_p);

		open C01;
		loop
		fetch C01 into
			ie_retorno_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
		end loop;
		close C01;
		end;
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
