CREATE OR REPLACE FUNCTION obter_se_caixa_estab (nr_seq_caixa_p bigint, cd_estab_corrente_p bigint) RETURNS varchar AS $body$
DECLARE


cd_estab_caixa_w			bigint;
cd_estab_financ_caixa_w			bigint;
cd_estab_financ_corrente_w		bigint;
ie_retorno_w				varchar(50);


BEGIN

select	cd_estabelecimento
into STRICT	cd_estab_caixa_w
from	caixa
where	nr_sequencia		= nr_seq_caixa_p;

select	cd_estab_financeiro
into STRICT	cd_estab_financ_caixa_w
from	estabelecimento
where	cd_estabelecimento	= cd_estab_caixa_w;

select	cd_estab_financeiro
into STRICT	cd_estab_financ_corrente_w
from	estabelecimento
where	cd_estabelecimento	= cd_estab_corrente_p;

ie_retorno_w			:= 'N';
if (cd_estab_caixa_w 		= cd_estab_corrente_p) then		-- Se o estabelecimento do caixa é igual ao estabelecimento corrente
	ie_retorno_w	:= 'S';
elsif (cd_estab_corrente_p 		= cd_estab_financ_caixa_w) then 	-- Se o estabelecimento corrente é o estabelecimento financeiro do caixa
	ie_retorno_w	:= 'S';
elsif (cd_estab_financ_corrente_w 	= cd_estab_caixa_w) then 		-- Se o estabelecimento financeiro corrente é o estabelecimento do caixa
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
