CREATE OR REPLACE FUNCTION obter_se_caixa_estrang (nr_seq_caixa_p bigint) RETURNS varchar AS $body$
DECLARE


ie_caixa_estrang_w	varchar(1) := 'N';
cont_w			bigint;


BEGIN
	select	count(*)
	into STRICT	cont_w
	from 	caixa_moedas
	where	nr_seq_caixa = nr_seq_caixa_p;

	if (coalesce(cont_w,0) > 0) then
		ie_caixa_estrang_w := 'S';
	else
		ie_caixa_estrang_w := 'N';
	end if;

	return ie_caixa_estrang_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
