CREATE OR REPLACE FUNCTION obter_se_caixa_liberado ( nr_seq_caixa_ori_p bigint, nr_seq_caixa_des_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(10) := 'S';
qt_registro_w double precision;

BEGIN

select	count(*)
into STRICT	qt_registro_w
from	caixa_lib_transferencia
where	nr_seq_caixa_ori = nr_seq_caixa_ori_p;

if (qt_registro_w > 0) then
	select count(*)
	into STRICT qt_registro_w
	from caixa_lib_transferencia
	where nr_seq_caixa_ori = nr_seq_caixa_ori_p
	and nr_seq_caixa_des = nr_seq_caixa_des_p;

	if (qt_registro_w = 0) then
		ds_retorno_w := 'N';
	else
		ds_retorno_w := 'S';
	end if;
end if;


return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
