CREATE OR REPLACE FUNCTION obter_se_caixa_prontuario ( nr_seq_caixa_p bigint, nr_prontuario_p bigint) RETURNS varchar AS $body$
DECLARE


ie_caixa_prontuario_w	smallint;
ie_opcao_prontuario_w	varchar(20);
ie_retorno_w		varchar(1) := 'N';


BEGIN
ie_opcao_prontuario_w := Obter_param_Usuario(0, 120, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_opcao_prontuario_w);

if (ie_opcao_prontuario_w = 'BASE') then

	begin
		select	1
		into STRICT	ie_caixa_prontuario_w
		from	same_prontuario a,
			pessoa_fisica p
		where	a.cd_pessoa_fisica 	= p.cd_pessoa_fisica
		and	p.nr_prontuario 	= nr_prontuario_p
		and	nr_seq_caixa 		= nr_seq_caixa_p  LIMIT 1;
	exception
		when	no_data_found then
			ie_caixa_prontuario_w := 0;
	end;

	if (ie_caixa_prontuario_w = 1) then
		ie_retorno_w := 'S';
	else
		ie_retorno_w := 'N';
	end if;

elsif (ie_opcao_prontuario_w = 'ESTAB') then
	begin
		select	1
		into STRICT	ie_caixa_prontuario_w
		from    same_prontuario a,
			pessoa_fisica p,
			pessoa_fisica_pront_estab c
		where   a.cd_pessoa_fisica      = p.cd_pessoa_fisica
		and     a.cd_pessoa_fisica      = c.cd_pessoa_fisica
		and     a.cd_estabelecimento    = c.cd_estabelecimento
		and     p.nr_prontuario         = nr_prontuario_p
		and	nr_seq_caixa 		= nr_seq_caixa_p  LIMIT 1;
	exception
		when	no_data_found then
			ie_caixa_prontuario_w := 0;
	end;

	if (ie_caixa_prontuario_w = 1) then
		ie_retorno_w := 'S';
	else
		ie_retorno_w := 'N';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
